(()=>{"use strict";let e=document.querySelector("#counter-result"),t=new class{constructor(){this.currentCount=0}increase(){this.currentCount++}decrease(){this.currentCount--,this.currentCount<0&&(alert("You can't go any further!"),this.reset())}update(e){e.textContent=this.currentCount}reset(){this.currentCount=0}};const r=document.querySelectorAll(".interaction");let n=function(r){"increase-counter"==r.target.id?t.increase():"decrease-counter"==r.target.id?t.decrease():t.reset(),t.update(e)};r.forEach((e=>e.addEventListener("click",n)))})();